import React, { useState } from "react";
import Input from "../../input/index";
import "./style.css";

import Autocomplete from "@mui/material/Autocomplete";
import TextField from "@mui/material/TextField";
import Stack from "@mui/material/Stack";

const SearchBar = ({ people, getQueryString, getTitleFilter }) => {
  const [values, setValues] = useState([
    {
      type: "text",
      placeholder: "Search",
      value: ""
    }
  ]);

  const handleChange = (val) => {
    let tempValues = values[0];
    tempValues = { ...tempValues, ...{ value: val } };
    setValues([tempValues]);

    const newData = people.filter((item) =>
      item.title.toLowerCase().includes(val.toLowerCase())
    );

    console.log(newData);
  };

  return (
    <div className="searchBar">
      <div className="search">
        <Input
          values={values}
          className={"searchItem"}
          handleChange={handleChange}
        />
      </div>
      <div className="filter">
        <Stack spacing={3} sx={{ width: 650 }}>
          <Autocomplete
            multiple
            id="size-small-outlined-multi"
            size="small"
            options={people}
            getOptionLabel={(option) => option.title}
            renderInput={(params) => (
              <TextField {...params} label="Filter" placeholder="Filter" />
            )}
          />
        </Stack>
      </div>
    </div>
  );
};

export default SearchBar;
