import React, { useState } from "react";
import Details from "../details";
import Input from "../input";
import { Table } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { useTheme } from "@mui/material/styles";
import Box from "@mui/material/Box";
import IconButton from "@mui/material/IconButton";
import FirstPageIcon from "@mui/icons-material/FirstPage";
import KeyboardArrowLeftIcon from "@mui/icons-material/KeyboardArrowLeft";
import KeyboardArrowRight from "@mui/icons-material/KeyboardArrowRight";
import LastPageIcon from "@mui/icons-material/LastPage";
import "./style.css";

const Footer = ({ people }) => {
  const [pageNumber, setPageNumber] = useState(0);

  const usersPerPage = 10;
  const pagesVisited = pageNumber * usersPerPage;

  const displayUsers = people.slice(pagesVisited, pagesVisited + usersPerPage);

  const pageCount = Math.ceil(people.length / usersPerPage);

  const theme = useTheme();

  const onPageChange = ({ selected }) => {
    setPageNumber(selected);
  };

  const handleFirstPageButtonClick = (event) => {
    onPageChange(event, 0);
  };

  const handleBackButtonClick = (event) => {
    onPageChange(event, pageNumber - 1);
  };

  const handleNextButtonClick = (event) => {
    onPageChange(event, pageNumber + 1);
  };

  const handleLastPageButtonClick = (event) => {
    onPageChange(event, Math.max(0, Math.ceil(pageCount / usersPerPage) - 1));
  };

  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const [values] = useState([
    {
      type: "text",
      placeholder: "Search"
    }
  ]);

  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  return (
    <div className="container">
      <div className="filter-container">
        <div className="search">
          <Input
            values={values}
            className={"searchBar"}
            handleChange={handleChange}
          />
        </div>
        <div className="filter">
          <select name="title" id="title">
            {displayUsers.map((item) => (
              <option
                key={item.id.toString()}
                value={item.title}
                placeholder="Filter"
              >
                {item.title}
              </option>
            ))}
          </select>
        </div>
      </div>
      <Table className="table">
        <thead>
          <tr>
            <th scope="col">Image</th>
            <th scope="col">Name</th>
            <th scope="col">Title</th>
            <th scope="col">Email</th>
            <th scope="col">Contact</th>
          </tr>
        </thead>
        <tbody>
          {displayUsers.map((item) => (
            <tr
              onClick={() => navigate("/people/details", <Details />)}
              key={item.id.toString()}
            >
              <td>{item.image}</td>
              <td>{item.name}</td>
              <td>{item.title}</td>
              <td>{item.email}</td>
              <td>{item.contact}</td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Box sx={{ flexShrink: 0, ml: 2.5 }}>
        <IconButton
          onClick={handleFirstPageButtonClick}
          disabled={pageNumber === 0}
          aria-label="first page"
        >
          {theme.direction === "rtl" ? <LastPageIcon /> : <FirstPageIcon />}
        </IconButton>
        <IconButton
          onClick={handleBackButtonClick}
          disabled={pageNumber === 0}
          aria-label="previous page"
        >
          {theme.direction === "rtl" ? (
            <KeyboardArrowRight />
          ) : (
            <KeyboardArrowLeftIcon />
          )}
        </IconButton>
        <IconButton
          onClick={handleNextButtonClick}
          disabled={pageNumber >= Math.ceil(pageCount / usersPerPage) - 1}
          aria-label="next page"
        >
          {theme.direction === "rtl" ? (
            <KeyboardArrowLeftIcon />
          ) : (
            <KeyboardArrowRight />
          )}
        </IconButton>
        <IconButton
          onClick={handleLastPageButtonClick}
          disabled={pageNumber >= Math.ceil(pageCount / usersPerPage) - 1}
          aria-label="last page"
        >
          {theme.direction === "rtl" ? <FirstPageIcon /> : <LastPageIcon />}
        </IconButton>
      </Box>
    </div>
  );
};

export default Footer;

