export const loginValidate = (values) => {
  const error = [...values];
  if (error.email.length === 0) {
    error.errorMessage = "Required!";
    error.error = true;
  }
  if (error.password.length === 0) {
    error.errorMessage = "Required!";
    error.error = true;
  } else if (error.value.length < 5) {
    error.errorMessage = "Must be 5 characters or more";
    error.error = true;
  } else {
    error.errorMessage = "";
  }

  return error;
};
