import React, { useState } from "react";
import Details from "../details";
import Input from "../input";
import { Table } from "react-bootstrap";
import BootstrapTable from "react-bootstrap-table-next";
import { useNavigate } from "react-router-dom";
import "./style.css";

const Footer = ({ people }) => {
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const [values] = useState([
    {
      type: "text",
      placeholder: "Search"
    }
  ]);

  const columns = [
    {
      dataField: "image",
      text: "Image"
    },
    {
      dataField: "name",
      text: "Name",
      sort: true
    },
    {
      dataField: "title",
      text: "Title",
      sort: true
    },
    {
      dataField: "email",
      text: "Email",
      sort: true
    },
    {
      dataField: "contact",
      text: "Contact",
      sort: true
    }
  ];

  const handleChange = (e) => {
    setSearch(e.target.value);
  };

  const rowEvents = { onclick: () => navigate("/people/details", <Details />) };

  return (
    <div className="container">
      <div className="filter-container">
        <div className="search">
          <Input
            values={values}
            className={"searchBar"}
            handleChange={rowEvents}
          />
        </div>
        <div className="filter">
          <select name="title" id="title">
            {people.map((item) => (
              <option
                key={item.id.toString()}
                value={item.title}
                placeholder="Filter"
              >
                {item.title}
              </option>
            ))}
          </select>
        </div>
      </div>
      <BootstrapTable
        bootstrap4
        keyField="id"
        data={people}
        columns={columns}
        bordered={false}
        handleClick={handleClick}
      />
    </div>
  );
};

export default Footer;

