import React, { useState } from "react";
import ReactPaginate from "react-paginate";
import "./style.css";

const Table = ({ people }) => {
  const [pageNumber, setPageNumber] = useState(0);
  const [values] = useState([
    {
      value: "10"
    },
    {
      value: "15"
    },
    {
      value: "20"
    },
    {
      value: "25"
    },
    {
      value: "30"
    }
  ]);

  const [usersPerPage, setUsersPerPage] = useState(0);
  const pagesVisited = pageNumber * usersPerPage;

  const displayUsers = people.slice(pagesVisited, pagesVisited + usersPerPage);

  const pageCount = Math.ceil(people.length / usersPerPage);

  const changePage = ({ selected }) => {
    setPageNumber(selected);
  };

  const handleSelect = (value) => {
    console.log(value);
    if (value[0] === 10) {
      setUsersPerPage(10);
    } else if (value[1] === 15) {
      setUsersPerPage(15);
    } else if (value[2] === 20) {
      setUsersPerPage(20);
    } else if (value[3] === 25) {
      setUsersPerPage(25);
    } else if (value[4] === 30) {
      setUsersPerPage(30);
    }
  };

  return (
    <div className="people-table">
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Image</th>
            <th scope="col">Name</th>
            <th scope="col">Title</th>
            <th scope="col">Email</th>
            <th scope="col">Contact</th>
          </tr>
        </thead>
        <tbody>
          {displayUsers.map((item) => (
            <tr key={item.id.toString()}>
              <td>{item.image}</td>
              <td>{item.name}</td>
              <td>{item.title}</td>
              <td>{item.email}</td>
              <td>{item.contact}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <select name="noOfUsers" id="noOfUsers" onSelect={handleSelect(values)}>
        {values.map((items) => (
          <option key={items.value.toString()} value={items.value}>
            {items.value}
          </option>
        ))}
      </select>
      <ReactPaginate
        previousLabel={"<"}
        nextLabel={">"}
        pageCount={pageCount}
        onPageChange={changePage}
      />
    </div>
  );
};

export default Table;
