import React, { useState } from "react";
import { Link } from "react-router-dom";
import Logo from "../../common/logo/index";
import Input from "../../common/input";
import "./style.css";

const Login = () => {
  const [values, setValues] = useState([
    {
      type: "email",
      value: "",
      error: false,
      errorMessage: ""
    },
    {
      type: "password",
      value: "",
      error: false,
      errorMessage: ""
    }
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();
    validator(values);
  };

  const validator = (values) => {
    const tempValues = [...values];

    if (values[0].value.length === 0) {
      tempValues[0].errorMessage = "Required!";
      tempValues[0].error = true;
      setValues(tempValues);
    } else {
      tempValues[0].errorMessage = "";
    }
    if (values[1].value.length === 0) {
      tempValues[1].errorMessage = "Required!";
      tempValues[1].error = true;
      setValues(tempValues);
    } else if (values[1].value.length < 5) {
      tempValues[1].errorMessage = "Must be 5 characters or more";
      tempValues[1].error = true;
      setValues(tempValues);
    } else {
      tempValues[1].errorMessage = "";
    }
  };

  return (
    <div className="app">
      <div className="form">
        <Logo />
        <form onSubmit={handleSubmit}>
          <div className="form-inner">
            <Input values={values} />
            {/* {values.map((item) => (
              <div key={item.type}>
                <div>
                  {item.error ? (
                    <input
                      className="error-form-input"
                      type={item.type}
                      name={item.type}
                      placeholder={item.type}
                    />
                  ) : (
                    <input
                      className="form-input"
                      type={item.type}
                      name={item.type}
                      placeholder={item.type}
                    />
                  )}

                  {item.error ? <p>{item.errorMessage}</p> : null}
                </div>
              </div>
            ))} */}
            <button className="login-button">SIGN IN</button>
            <Link className="login-link" to="/signup">
              Create account!
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
