import React, { useState } from "react";
import { Link } from "react-router-dom";
import Logo from "../../common/logo";
import Input from "../../common/input";
import "./style.css";

const Signup = () => {
  const [values, setValues] = useState([
    {
      type: "text",
      placeholder: "name",
      value: "",
      error: false,
      errorMessage: ""
    },
    {
      type: "text",
      placeholder: "title",
      value: "",
      error: false,
      errorMessage: ""
    },
    {
      type: "text",
      placeholder: "contact",
      value: "",
      error: false,
      errorMessage: ""
    },
    {
      type: "email",
      placeholder: "email",
      value: "",
      error: false,
      errorMessage: ""
    },
    {
      type: "password",
      placeholder: "password",
      value: "",
      error: false,
      errorMessage: ""
    },
    {
      type: "password",
      placeholder: "confirm password",
      value: "",
      error: false,
      errorMessage: ""
    }
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();
    validator(values);
  };

  const validator = (values) => {
    const tempValue = [...values];

    if (values[0].value.length === 0) {
      tempValue[0].errorMessage = "Required!";
      tempValue[0].error = true;
      setValues(tempValue);
    }
    if (values[1].value.length === 0) {
      tempValue[1].errorMessage = "Required!";
      tempValue[1].error = true;
      setValues(tempValue);
    }
    if (values[2].value.length === 0) {
      tempValue[2].errorMessage = "Required!";
      tempValue[2].error = true;
      setValues(tempValue);
    }
    if (values[3].value.length === 0) {
      tempValue[3].errorMessage = "Required!";
      tempValue[3].error = true;
      setValues(tempValue);
    }
    if (values[4].value.length === 0) {
      tempValue[4].errorMessage = "Required!";
      tempValue[4].error = true;
      setValues(tempValue);
    } else if (values[4].value.length < 5) {
      tempValue[4].errorMessage = "Must be 5 characters or more";
      tempValue[4].error = true;
      setValues(tempValue);
    }
    if (values[5].value.length === 0) {
      tempValue[5].errorMessage = "Required!";
      tempValue[5].error = true;
      setValues(tempValue);
    }
  };

  return (
    <div className="app">
      <div className="form">
        <Logo />
        <form onSubmit={handleSubmit}>
          <div className="signup-form-inner">
            <Input values={values} />
            <button className="signup-button">SIGN UP</button>
            <Link className="signup-link" to="/login">
              Login!
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Signup;
