export const loginValidate = (values) => {
  const error = [...values];
  if (values[0].value.length === 0) {
    error[0].errorMessage = "Required!";
    error[0].error = true;
  } else {
    error[0].errorMessage = "";
  }
  if (values[1].value.length === 0) {
    error[1].errorMessage = "Required!";
    error[1].error = true;
  } else if (values[1].value.length < 5) {
    error[1].errorMessage = "Must be 5 characters or more";
    error[1].error = true;
  } else {
    error[1].errorMessage = "";
  }

  return error;
};
